var chkjs = {
    'chkC': (cname) => {
        if (getCookie(cname) != "") {
            window.location.href = "/pages/app/dashboard/";
        } else {
            window.location.href = "/pages/app/login/";
        }
    },
    'redir': (url) => {
        window.location.href = url;
    },
    'chkCourse': (cid, data) => {
        const course = data.courses.find(course => course.cid === cid);
        return course;
    }
}
